"use strict";

console.log(
	";!knitout-2\n"+
	";;Carriers: A B C\n"+
	"; generated by write-test.js\n"
);

["tuck", "knit", "miss"].forEach(function(op){
["", "A", "A B", "A B C"].forEach(function(yarns){
["+","-"].forEach(function(dir){
	let n;
	let y;
	if (dir === '-') {
		n = ["f0", "f1", "f2", "f3"];
		y = ["", "A", "A B", "A B C"];
	} else {
		n = ["f3", "f2", "f1", "f0"];
		y = ["", "A", "A B", "A B C"];
	}
	let d2 = (dir == '+' ? '-' : '+');
	console.log(
		"\n"+
		"; " + op + " " + dir + " " + yarns + "\n"+
		"inhook A B C\n"+
		"tuck " + d2 + " " + n[0] + " " + y[0] + "\n"+
		"tuck " + d2 + " " + n[1] + " " + y[1] + "\n"+
		"tuck " + d2 + " " + n[2] + " " + y[2] + "\n"+
		"tuck " + d2 + " " + n[3] + " " + y[3] + "\n"+
		op + " " + dir + " " + n[3] + " " + yarns + "\n"+
		op + " " + dir + " " + n[2] + " " + yarns + "\n"+
		op + " " + dir + " " + n[1] + " " + yarns + "\n"+
		op + " " + dir + " " + n[0] + " " + yarns + "\n"+
		"drop f0\n"+
		"drop f1\n"+
		"drop f2\n"+
		"drop f3\n"+
		"outhook A B C"
	);
});
});
});
